// src/components/airports/AirportForm.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import {
  Paper,
  TextField,
  Button,
  Grid,
  Typography,
  Box,
  CircularProgress,
} from '@mui/material';
import { useAirports } from '../../hooks/useAirports';

const AirportForm = () =&gt; {
  const { id } = useParams();
  const navigate = useNavigate();
  const { getAirportById, createAirport, updateAirport, loading } = useAirports();

  const [formData, setFormData] = useState({
    name: '',
    code: '',
    city: '',
    country: '',
  });

  const [errors, setErrors] = useState({});
  const isEditMode = Boolean(id);

  useEffect(() =&gt; {
    const fetchAirport = async () =&gt; {
      if (isEditMode) {
        const airport = await getAirportById(id);
        if (airport) {
          setFormData({
            name: airport.name,
            code: airport.code,
            city: airport.city,
            country: airport.country,
          });
        }
      }
    };

    fetchAirport();
  }, [getAirportById, id, isEditMode]);

  const validateForm = () =&gt; {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (!formData.code.trim()) {
      newErrors.code = 'Code is required';
    } else if (!/^[A-Z]{3}$/.test(formData.code)) {
      newErrors.code = 'Code must be 3 uppercase letters';
    }

    if (!formData.city.trim()) {
      newErrors.city = 'City is required';
    }

    if (!formData.country.trim()) {
      newErrors.country = 'Country is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) =&gt; {
    const { name, value } = e.target;
    setFormData((prev) =&gt; ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) =&gt; {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    let result;

    if (isEditMode) {
      result = await updateAirport(id, formData);
    } else {
      result = await createAirport(formData);
    }

    if (result) {
      navigate('/airports');
    }
  };

  return (
    &lt;Paper sx={{ p: 3 }}&gt;
      &lt;Typography variant="h5" gutterBottom&gt;
        {isEditMode ? 'Edit Airport' : 'Add New Airport'}
      &lt;/Typography&gt;

      &lt;form onSubmit={handleSubmit}&gt;
        &lt;Grid container spacing={3}&gt;
          &lt;Grid item xs={12} sm={6}&gt;
            &lt;TextField
              fullWidth
              label="Name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              error={Boolean(errors.name)}
              helperText={errors.name}
            /&gt;
          &lt;/Grid&gt;

          &lt;Grid item xs={12} sm={6}&gt;
            &lt;TextField
              fullWidth
              label="Code (3 letters)"
              name="code"
              value={formData.code}
              onChange={handleChange}
              error={Boolean(errors.code)}
              helperText={errors.code}
              inputProps={{ maxLength: 3, style: { textTransform: 'uppercase' } }}
            /&gt;
          &lt;/Grid&gt;

          &lt;Grid item xs={12} sm={6}&gt;
            &lt;TextField
              fullWidth
              label="City"
              name="city"
              value={formData.city}
              onChange={handleChange}
              error={Boolean(errors.city)}
              helperText={errors.city}
            /&gt;
          &lt;/Grid&gt;

          &lt;Grid item xs={12} sm={6}&gt;
            &lt;TextField
              fullWidth
              label="Country"
              name="country"
              value={formData.country}
              onChange={handleChange}
              error={Boolean(errors.country)}
              helperText={errors.country}
            /&gt;
          &lt;/Grid&gt;

          &lt;Grid item xs={12}&gt;
            &lt;Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 2 }}&gt;
              &lt;Button 
                variant="outlined" 
                onClick={() =&gt; navigate('/airports')}
              &gt;
                Cancel
              &lt;/Button&gt;
              &lt;Button 
                type="submit" 
                variant="contained" 
                color="primary"
                disabled={loading}
              &gt;
                {loading ? &lt;CircularProgress size={24} /&gt; : isEditMode ? 'Update' : 'Create'}
              &lt;/Button&gt;
            &lt;/Box&gt;
          &lt;/Grid&gt;
        &lt;/Grid&gt;
      &lt;/form&gt;
    &lt;/Paper&gt;
  );
};

export default AirportForm;




