import java.util.HashMap;

@RestController
@RequestMapping("/api/book")
public class BookRestController {

    @Autowired
    BookService bookService;

    @GetMapping ("/books")
    public HashMap&lt;String, Book&gt; getAllBooks(){

      return bookService.getAllBooks();


    }
}

