import axios from "axios";

///`${API_URL}/todo`
// https://github.com/mockapi-io/docs/wiki/Quick-start-guide
// API_URL mockapi.io
//const API_BASE_URL = "https://645fbe78ca2d89f7e74cf4c9.mockapi.io/v1";

const API_BASE_URL = "http://localhost:9898";

const TodoService = {
  getAllTodos: async () =&gt; {
    try {
      const response = await axios.get(`${API_BASE_URL}/todo`);
      //console.log("retrieving todos:", response.data);
      return response.data;
    } catch (error) {
      console.error("Error retrieving todos:", error);
      throw error;
    }
  },

  createTodo: async (todo) =&gt; {
    try {
      const response = await axios.post(`${API_BASE_URL}/todo/createTodo`, todo);
      console.error("Data:", response.data);
      return response.data;
    } catch (error) {
      console.error("Error creating todo:", error);
      throw error;
    }
  },

  updateTodo: async (todo) =&gt; {
    try {
      const response = await axios.put(`${API_BASE_URL}/todo/updateTodo/${todo.id}`, todo);
      return response.data;
    } catch (error) {
      console.error("Error updating todo:", error);
      throw error;
    }
  },

  deleteTodo: async (todoId) =&gt; {
    try {
      const response = await axios.delete(`${API_BASE_URL}/todo?id=${todoId}`);
      return response.data;
    } catch (error) {
      console.error("Error deleting todo:", error);
      throw error;
    }
  },
};

export default TodoService;



TodoContext.js

import React, { createContext, useState, useEffect } from "react";
import TodoService from "./TodoService";

const TodoContext = createContext();

const TodoProvider = ({ children }) =&gt; {
  const [todos, setTodos] = useState([]);

  //
  useEffect(() =&gt; {
    fetchTodos();
  }, []);

  //
  const fetchTodos = async () =&gt; {
    try {
      const todos = await TodoService.getAllTodos();
      setTodos(todos);
      //console.log("todos:", todos);
    } catch (error) {
      console.error("Error fetching todos:", error);
    }
  };

  //
  const createTodo = async (todo) =&gt; {
    try {
      const createdTodo = await TodoService.createTodo(todo);
      setTodos((prevTodos) =&gt; [...prevTodos, createdTodo]);
    } catch (error) {
      console.error("Error creating todo:", error);
    }
  };

 const updateTodo = async (todoToUpdate) =&gt; {
   try {
     const updatedTodo = {
       ...todoToUpdate,
       completed: !todoToUpdate.completed,
     };
     await TodoService.updateTodo(updatedTodo);
     setTodos((prevTodos) =&gt; {
       const updatedTodos = [...prevTodos];
       const todoIndex = updatedTodos.findIndex(
         (todo) =&gt; todo.id === updatedTodo.id
       );
       updatedTodos[todoIndex] = updatedTodo;
       return updatedTodos;
     });
   } catch (error) {
     console.error("Error updating todo:", error);
   }
 };

  //
  const deleteTodo = async (todoId) =&gt; {
    try {
      await TodoService.deleteTodo(todoId);
      setTodos((prevTodos) =&gt; prevTodos.filter((todo) =&gt; todo.id !== todoId));
    } catch (error) {
      console.error("Error deleting todo:", error);
    }
  };

  //
  return (
    &lt;TodoContext.Provider value={{ todos, createTodo, updateTodo, deleteTodo }}&gt;
      {children}
    &lt;/TodoContext.Provider&gt;
  );
};

export { TodoContext, TodoProvider };

2 Why do we need it

Array destructuring can be a useful tool for extracting values from arrays and assigning them to separate variables. It can make your code more concise and easier to read, especially when working with large or complex arrays.

Here is the old way of assigning array items to a variable:


App.js

const vehicles = ['mustang', 'f-150', 'expedition'];

// old way
const car = vehicles[0];
const truck = vehicles[1];
const suv = vehicles[2];

Here is the new way of assigning array items to a variable With destructuring:


App.js

const vehicles = ['mustang', 'f-150', 'expedition'];

const [car, truck, suv] = vehicles;

4 More examples

4.1 Nested arrays
You can use destructuring to extract values from nested arrays:


App.js

const arr = [[1, 2], [3, 4]];
const [[a, b], [c, d]] = arr;

console.log(a); // 1
console.log(b); // 2
console.log(c); // 3
console.log(d); // 4



4.2 Rest syntax
The rest syntax allows you to capture the remaining elements of an array in a separate variable:


App.js

const arr = [1, 2, 3, 4, 5];
const [a, b, ...rest] = arr;

console.log(a); // 1
console.log(b); // 2
console.log(rest); // [3, 4, 5]



4.3 Swapping variables
You can use destructuring to easily swap the values of two variables:


App.js

let a = 1;
let b = 2;

[a, b] = [b, a];

console.log(a); // 2
console.log(b); // 1



4.4 Function arguments
You can use destructuring to specify function arguments:


App.js

function add([a, b]) {
  return a + b;
}

console.log(add([1, 2])); // 3



4.5 Object destructuring
You can also use destructuring with objects. Hereâ€™s an example:


App.js

const obj = {
  foo: 1,
  bar: 2
};

const { foo, bar } = obj;

console.log(foo); // 1
console.log(bar); // 2

